insert into smalldata(bdid,mdid,text,content) values(1,1,"XML","XML은 W3C에서 개발된  다른 특수한 목적을 갖는 마크업 언어를 만드는데 사용하도록 권장하는 다목적 마크업 언어이다. XML은 SGML의 단순화된 부분집합으로  다른 많은 종류의 데이터를 기술하는 데 사용할 수 있다. XML은 주로 다른 종류의 시스템  특히 인터넷에 연결된 시스템끼리 데이터를 쉽게 주고 받을 수 있게 하여 HTML의 한계를 극복할 목적으로 만들어졌다.");
insert into smalldata(bdid,mdid,text,content) values(1,1,"XHTML","XHTML은 HTML과 동등한 표현 능력을 지닌 마크업 언어로  HTML보다 엄격한 문법을 가진다. HTML이 SGML의 응용인 데 반해  매우 유연한 마크업 언어인 XHTML은 SGML의 제한된 부분집합인 XML의 응용이다. XHTML 문서는 하나의 XML 문서로서 문법적으로 정확해야 하기 때문에  HTML과 달리 표준 XML 라이브러리를 이용한 자동화된 처리가 가능하다.");
insert into middledata(bdid,text,content) values(1,"스타일 시트","스타일 시트는 웹 디자인을 위한 표현과 컨텐츠의 분리 형태로  웹페이지의 시각적 레이아웃(스타일)을 정의합니다. 스타일시트 언어를 사용하여 외부스타일 시트 파일에 정의됩니다.");
insert into smalldata(bdid,mdid,text,content) values(1,2,"CSS","마크업 언어가 실제 표시되는 방법을 기술하는 언어로   HTML과 XHTML에 주로 쓰이며  XML에서도 사용할 수 있다. W3C의 표준이며  레이아웃과 스타일을 정의할 때의 자유도가 높다.CSS는 옷과 액세서리처럼 꾸미는 역할을 담당한다고 할 수 있다. 즉  HTML 구조는 그대로 두고 CSS 파일만 변경해도 전혀 다른 웹사이트처럼 꾸밀 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(1,2,"XSL","XML 문서는 다양한 장치에서 데이터를 저장하고 전달할 목적으로 만들어졌습니다.CSS가 HTML 문서를 위한 스타일 시트 언어라면  XSL은 XML 문서를 위한 스타일 시트 언어입니다.");
insert into smalldata(bdid,mdid,text,content) values(1,2,"Sass","Sass는 햄튼 캐틀린이 설계하고 나탈리 바이첸바움이 개발한 스타일시트 언어이다. 초기 버전들 이후에 바이첸바움과 크리스 엡스타인은 Sass 파일에 쓰이는 단순 스크립팅 언어인 SassScript로 Sass의 확장을 계속하였다. ");
insert into middledata(bdid,text,content) values(1,"스크립트 언어","스크립트 언어(scripting language)란 응용 소프트웨어를 제어하는 컴퓨터 프로그래밍 언어를 가리킨다. 스크립트 프로그래밍 언어라고도 한다. 스크립트 언어는 응용 프로그램과 독립하여 사용되고 일반적으로 응용 프로그램의 언어와 다른 언어로 사용되어 최종사용자가 응용 프로그램의 동작을 사용자의 요구에 맞게 수행할 수 있도록 해준다. ");
insert into smalldata(bdid,mdid,text,content) values(1,3,"JS","자바스크립트는 객체 기반의 스크립트 프로그래밍 언어이다. 이 언어는 웹 브라우저 내에서 주로 사용하며  다른 응용 프로그램의 내장 객체에도 접근할 수 있는 기능을 가지고 있다. 또한 Node.js와 같은 런타임 환경과 같이 서버 프로그래밍에도 사용되고 있다. ");
insert into smalldata(bdid,mdid,text,content) values(1,3,"Ruby","루비는 마츠모토 유키히로가 개발한 동적 객체 지향 스크립트 프로그래밍 언어이다. 루비는 순수 객체 지향 언어라  정수나 문자열 등을 포함한 데이터 형식 등 모든 것이 객체이다.");
insert into middledata(bdid,text,content) values(1,"버전 관리 시스템","버전 관리  소스 관리  소스 코드 관리란 동일한 정보에 대한 여러 버전을 관리하는 것을 말한다. ");
insert into smalldata(bdid,mdid,text,content) values(1,4,"Git ","깃은 컴퓨터 파일의 변경사항을 추적하고 여러 명의 사용자들 간에 해당 파일들의 작업을 조율하기 위한 분산 버전 관리 시스템이다. 소프트웨어 개발에서 소스 코드 관리에 주로 사용되지만 어떠한 집합의 파일의 변경사항을 지속적으로 추적하기 위해 사용될 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(1,4,"Github","컴퓨터 프로그램 소스를 공유하고 협업하여 개발할 수 있는 버전 관리 시스템인 깃(Git)에 프로젝트 관리 지원 기능을 확장하여 제공하는 웹 호스팅 서비스.");
insert into smalldata(bdid,mdid,text,content) values(1,4,"Gitlab","깃랩은 깃랩 사가 개발한 깃 저장소 및 CI/CD  이슈 추적  보안성 테스트 등의 기능을 갖춘 웹 기반의 데브옵스 플랫폼으로써  오픈 소스 라이선스 및 사유 소프트웨어 라이선스를 사용한다.");
insert into middledata(bdid,text,content) values(1,"패키지 매니저","패키지 관리자  패키지 관리 시스템은 컴퓨터의 운영 체제를 위해 일정한 방식으로 컴퓨터 프로그램의 설치  업그레이드  구성  제거 과정을 자동화하는 소프트웨어 도구들의 모임이다. 패키지 관리자는 아카이브 파일로 된 소프트웨어 배포판과 데이터인 패키지를 다룬다.");
insert into smalldata(bdid,mdid,text,content) values(1,5,"npm","npm 은 자바스크립트 프로그래밍 언어를 위한 패키지 관리자이다. 자바스크립트 런타임 환경 Node.js의 기본 패키지 관리자이다. 명령 줄 클라이언트  그리고 공개 패키지와 지불 방식의 개인 패키지의 온라인 데이터베이스로 이루어져 있다.");
insert into smalldata(bdid,mdid,text,content) values(1,5,"yarn","Yarn은 프로젝트 매니저를 겸직하는 패키지 매니저입니다. 원샷 프로젝트에서 일하든  대규모 모노레포즈에서 일하든  취미로 일하든 엔터프라이즈 사용자로 일하든  NAT은 귀사에 대한 지원을 제공합니다.");
insert into middledata(bdid,text,content) values(1,"빌드 도구","빌드 도구는 소스 코드에서 실행 가능한 응용 프로그램 생성을 자동화하는 프로그램입니다. 빌딩은 사용 가능하거나 실행 가능한 형태로 코드를 컴파일  링크 및 패키징하는 것을 통합합니다... 자동화 도구를 사용하면 빌드 프로세스의 일관성을 향상시킬 수 있습니다.");
insert into smalldata(bdid,mdid,text,content) values(1,6,"npm scripts","스크립트를 사용하여 반복 작업을 자동화합니다. 예를 들어 프로젝트를 빌드하고 CSS(Cascading Style Sheet) 및 JS(JavaScript) 파일을 최소화합니다. 스크립트는 임시 파일 및 폴더 삭제에도 사용됩니다.");
insert into middledata(bdid,text,content) values(1,"포매터","Linters and formatters 는 개발 팀에서 코딩 패턴을 따르는 데 도움이 되는 소스 코드를 확인  감지 및 수정할 수 있는 유용한 도구입니다. 프로젝트에 린터 도구를 통합하여 읽기 쉽고  읽기 쉽고  오염이 적고  유지 관리가 용이한 소스 코드를 얻을 수 있습니다.");
insert into smalldata(bdid,mdid,text,content) values(1,7,"Prettier ","Prettier 는 자동으로 코드의 스타일을 관리해주는 도구입니다. 가령  문자열을 사용 할 때  를 쓸지  를 쓸지  또는 세미콜론 (;) 를 코드 뒤에 붙일지 말지  들여쓰기는 얼마나 할지  이런 것들을 관리해줄 수 있습니다. ");
insert into smalldata(bdid,mdid,text,content) values(1,7,"ESLint","ESLint는 JavaScript 코드에서 발견 된 문제 패턴을 식별하기위한 정적 코드 분석 도구입니다. ESLint의 규칙은 구성 가능하며 사용자 정의 된 규칙을 정의하고로드 할 수 있습니다. ESLint는 코드 품질과 코딩 스타일 문제를 모두 다룹니다.");
insert into middledata(bdid,text,content) values(1,"모듈 번들러","Module Bundlers는 모듈 번들러(module bundler)는 자바스크립트의 일부와 그 종속성을 가져와서 하나의 파일로 묶는 도구이다. 브라우저 지정  웹 팩  롤업 등의 도구를 사용했을 수 있습니다.");
insert into smalldata(bdid,mdid,text,content) values(1,8,"Webpack","웹팩은 오픈 소스 자바스크립트 모듈 번들러이다. 주로 자바스크립트를 위한 모듈 번들러이지만 호환 플러그인을 포함하는 경우 HTML  CSS  심지어는 이미지와 같은 프론트엔드 자산들을 변환할 수 있다. 웹팩은 의존성이 있는 모듈을 취하여 해당 모듈을 대표하는 정적 자산들을 생성한다.");
insert into smalldata(bdid,mdid,text,content) values(1,8,"Rollup","롤업은 작은 코드 조각들을 라이브러리나 응용 프로그램과 같이 더 크고 복잡한 것으로 컴파일하는 자바스크립트용 모듈 번들러이다. 그것은 CommonJS와 AMD와 같은 이전의 독특한 솔루션 대신 자바스크립트의 ES6 개정판에 포함된 코드 모듈들을 위한 새로운 표준화된 포맷을 사용한다. ES 모듈은 당신이 좋아하는 라이브러리에서 가장 유용한 개별 기능들을 자유롭고 원활하게 결합할 수 있게 해준다. ");
insert into smalldata(bdid,mdid,text,content) values(1,8,"Parcel","모듈 번들러란 웹 애플리케이션을 동작시키기 위한 서로 연관 관계가 있는 웹 구성 자원(HTML  CSS  Javscript  Images 등)을 모두 각각의 모듈로 보고 이들의 의존성을 묶고 조합해서 합쳐진 하나의 결과물(static한 자원)을 만드는 도구입니다. Parcel은 워커 프로세스를 이용하여 멀티코어 컴파일을 가능케 하고  심지어 재시작 후라 해도 빠른 리빌드를 할 수 있도록 파일시스템 캐시를 갖고 있습니다. JS  CSS  HTML  파일 애셋  그 외 많은 것들에 대한 지원을 즉시 제공합니다.");
insert into middledata(bdid,text,content) values(1,"Framework ","컴퓨터 프로그래밍에서  소프트웨어 프레임워크는 복잡한 문제를 해결하거나 서술하는 데 사용되는 기본 개념 구조이다. 간단히 뼈대  골조  프레임워크라고도 한다. 이렇게 매우 폭넓은 정의는 이 용어를 버즈워드로서  특히 소프트웨어 환경에서 사용할 수 있게 만들어 준다.");
insert into smalldata(bdid,mdid,text,content) values(1,9,"React.js ","컴퓨팅에서 리액트는 자바스크립트 라이브러리의 하나로서 사용자 인터페이스를 만들기 위해 사용된다. 페이스북과 개별 개발자 및 기업들 공동체에 의해 유지보수된다. 리액트는 싱글 페이지 애플리케이션이나 모바일 애플리케이션의 개발 시 토대로 사용될 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(1,9,"Angular","앵귤러는 구글의 앵귤러 팀과 개인 및 기업 공동체에 의해 주도되는 타입스크립트 기반 오픈 소스 프론트엔드 웹 애플리케이션 프레임워크이다. 앵귤러는 AngularJS를 개발한 동일 팀으로부터 완전히 다시 작성한 것이다.");
insert into smalldata(bdid,mdid,text,content) values(1,9,"Vue.js","Vue.js는 웹 애플리케이션의 사용자 인터페이스를 만들기 위해 사용하는 오픈 소스 프로그레시브 자바스크립트 프레임워크이다.");
insert into middledata(bdid,text,content) values(1,"CSS Frameworks","CSS 프레임 워크는 CSS (Cascading Style Sheets) 언어를 사용하여보다 쉽고 표준을 준수하는 웹 디자인을 가능하게하는 라이브러리입니다. 이러한 프레임 워크의 대부분은 최소한 그리드를 포함합니다.");
insert into smalldata(bdid,mdid,text,content) values(1,10,"Reactstrap","Reactstrap은 사전 빌드된 부트스트랩 4 구성 요소를 제공하여 뛰어난 유연성과 사전 빌드 검증을 가능하게 합니다. 이를 통해 우리는 인상적이고 직관적인 사용자 경험을 제공하도록 보장되는 아름다운 양식을 신속하게 구축할 수 있습니다.");
insert into smalldata(bdid,mdid,text,content) values(1,10,"Material UI ","Material-UI는 구글의 Material Design을 구현하는 React 구성 요소를 특징으로 하는 오픈 소스 프로젝트이다. 리액트가 대중에게 공개된 지 얼마 되지 않은 2014년에 킥을 시작해  그 후부터 인기가 높아지고 있다.");
insert into smalldata(bdid,mdid,text,content) values(1,10,"Tailwind CSS","Tailwind CSS는 사용자 지정이 가능한 저수준 CSS 프레임 워크로  재정의를 위해 싸워야하는 성가신 스타일없이 맞춤형 디자인을 작성하는 데 필요한 모든 빌딩 블록을 제공하고 있습니다.");
insert into smalldata(bdid,mdid,text,content) values(1,10,"Chakra UI","Chakra UI는 Retact 애플리케이션을 구축하는 데 필요한 구성 요소를 제공하는 단순 모듈식 액세스 가능한 구성 요소 라이브러리입니다.");
insert into smalldata(bdid,mdid,text,content) values(1,10,"Bootstrap","부트스트랩은 웹사이트를 쉽게 만들 수 있게 도와주는 HTML  CSS  JS 프레임워크이다. 하나의 CSS로 휴대폰  태블릿  데스크탑까지 다양한 기기에서 작동한다. 다양한 기능을 제공하여 사용자가 쉽게 웹사이트를 제작  유지  보수할 수 있도록 도와준다.");
insert into middledata(bdid,text,content) values(1,"Testing","Testing your App ");
insert into smalldata(bdid,mdid,text,content) values(1,11,"Jest","Jest는 대규모 웹 응용 프로그램에 대한 단순성과 지원에 중점을두고 Christoph Nakazawa가 설계 및 구축 한 Facebook  Inc.에서 유지 관리하는 JavaScript 테스트 프레임 워크입니다.");
insert into smalldata(bdid,mdid,text,content) values(1,11,"react-testing-library","반응 테스트 라이브러리는 반응 구성 요소를 테스트하기 위한 매우 가벼운 솔루션입니다. 반응 영역 및 반응 영역/테스트 유틸리티 위에 더 나은 테스트 관행을 장려하는 방식으로 가벼운 유틸리티 기능을 제공합니다. ... 이 라이브러리가 제공하는 유틸리티는 사용자와 동일한 방식으로 DOM을 쉽게 쿼리할 수 있습니다.");
insert into smalldata(bdid,mdid,text,content) values(1,11,"Cypress","cypress는 end to end test framework입니다.end to end test란 사용자 입장에서 테스트하는 것을 말합니다.필요한 이유는 여러가지이지만 일단 가장 큰 이유는 코드를 작성하고 테스트할때 휴대폰인증 등 노트북 자체에서는 다 할 수 없는 테스트를 제외하고는 e2e test(end to end test)로 해결 가능하기 때문에 생산성을 더 높일 수 있기 때문입니다.");
insert into smalldata(bdid,mdid,text,content) values(1,11,"Enzyme","enzyme은 React 컴포넌트 테스팅 도구로 컴포넌트의 마크업이 복잡할수록 테스트 코드도 복잡해지기 마련인데 추상화 API를 통해 깔끔하게 마크업을 확인할 수 있는 API를 제공한다.");
insert into middledata(bdid,text,content) values(1,"Type checkers","Type checker는 유형의 제약 조건을 확인하고 적용하는 프로세스이며  컴파일 시간(즉  정적) 또는 런타임(즉  동적)에 발생할 수 있습니다. 유형 검사는 프로그램이 유형 안전하다는 것을 확인하는 것으로  유형 오류의 가능성을 최소한으로 유지하는 것을 의미합니다.");
insert into smalldata(bdid,mdid,text,content) values(1,12,"TypeScript","타입스크립트는 자바스크립트의 슈퍼셋인 오픈소스 프로그래밍 언어이다. 마이크로소프트에서 개발  유지하고 있으며 엄격한 문법을 지원한다. C#의 리드 아키텍트이자 델파이  터보 파스칼의 창시자인 Anders Hejlsberg가 개발에 참여한다.");
