insert into smalldata(bdid,mdid,text,content) values(1,13,"Next.js","Next.js는 서버 측 렌더링 및 React 기반 웹 애플리케이션을위한 정적 웹 사이트 생성과 같은 기능을 지원하는 오픈 소스 React 프런트 엔드 개발 웹 프레임 워크입니다.");
insert into smalldata(bdid,mdid,text,content) values(1,13,"Universal","Angular Universal은 응용 프로그램을 서버의 HTML로 렌더링(즉  Node.js)하는 프로세스입니다. 일반적인 Angular 애플리케이션은 브라우저에서 렌더링이 발생하는 단일 페이지 애플리케이션(일명 SPA)입니다. 이 프로세스를 CSR(클라이언트 사이드 렌더링)이라고도 합니다.");
insert into smalldata(bdid,mdid,text,content) values(1,13,"Nuxt.js","Nuxt.js는 Vue.js  Node.js  Webpack 및 Babel.js를 기반으로하는 무료 오픈 소스 웹 애플리케이션 프레임 워크입니다. 프레임 워크는 범용 애플리케이션을위한 메타 프레임 워크로 광고됩니다.");
insert into middledata(bdid,text,content) values(1,"그래프 QL","그래프QL은 페이스북이 2012년에 개발하여 2015년에 공개적으로 발표된 데이터 질의어이다. 그래프QL은 REST 및 부속 웹서비스 아키텍쳐를 대체할 수 있다. 클라이언트는 필요한 데이터의 구조를 지정할 수 있으며  서버는 정확히 동일한 구조로 데이터를 반환한다.");
insert into smalldata(bdid,mdid,text,content) values(1,14,"Apollo","아폴로 서버는 확장 가능한 오픈 소스 자바스크립트 그래프QL 서버이다. 이를 통해 그래프에서 사용할 수 있는 모든 유형과 필드를 지정하는 그래프QL 스키마를 정의할 수 있습니다. 스키마의 각 필드를 백엔드 데이터 원본의 데이터로 채우는 방법을 지정하는 해결사 모음입니다.");
insert into smalldata(bdid,mdid,text,content) values(1,14,"Relay  Modern","Relay는 데이터 기반 애플리케이션을 구축하기 위한 JavaScript 프레임워크입니다. 합성 가능한 사용자 인터페이스를 위한 React와 합성 가능한 데이터 페팅을 위한 GraphQL을 결합한다.");
insert into middledata(bdid,text,content) values(1,"정적 사이트 생성기","정적 사이트 생성기는 템플릿이나 구성 요소 및 지정된 콘텐트 원본에서 HTML 페이지를 생성하는 소프트웨어 응용 프로그램입니다.");
insert into smalldata(bdid,mdid,text,content) values(1,15,"Next.js","Next.js는 React의 SSR(Server Side Rendering)을 쉽게 구현할 수 있게 도와주는 간단한 프레임워크입니다. ");
insert into smalldata(bdid,mdid,text,content) values(1,15,"GatsbyJS","GatsbyJS는 반응 기반의 그래프QL 구동 정적 사이트 생성기이다. 강력한 사전 구성을 사용하여 놀라울 정도로 빠른 페이지 로드  서비스 작업자  코드 분할  서버 측 렌더링  지능형 이미지 로드  자산 최적화 및 데이터 프리페칭에 정적 파일만 사용하는 웹 사이트를 구축합니다.");
insert into smalldata(bdid,mdid,text,content) values(1,15,"Nuxt.js","Nuxt.js는 Vue.js  Node.js  Webpack 및 Babel.js를 기반으로하는 무료 오픈 소스 웹 애플리케이션 프레임 워크입니다. 프레임 워크는 범용 애플리케이션을위한 메타 프레임 워크로 광고됩니다.");
insert into smalldata(bdid,mdid,text,content) values(1,15,"Vuepress","VuePress는 Vue를 만든 Evan You의 최소화된 Vue-powered 정적 사이트 생성기입니다. VuePress 사이트는 SPA이며 빌드하는 동안 각 경로를 방문하여 서버 렌더링 버전을 만듭니다. VuePress는 Vue  Vue Router 및 Webpack에 의해 구동됩니다. 당신은 또한 당신의 마크다운 안에서 Vue를 사용할 수 있습니다!");
insert into smalldata(bdid,mdid,text,content) values(1,15,"Jekyll","Jekyll은 개인  프로젝트 또는 조직 사이트를위한 간단한 블로그 인식 정적 사이트 생성기입니다. GitHub의 공동 설립자 인 Tom Preston-Werner가 루비로 작성했으며 오픈 소스 MIT 라이센스에 따라 배포됩니다.");
insert into smalldata(bdid,mdid,text,content) values(1,15,"Hugo","휴고는 Go로 작성된 정적 사이트 생성기이다. 2013년 Steve Francia에 의해 처음 개발된 휴고는 현재의 수석 개발자 Bjørn Erik Pedersen의 도움을 받아 기능  성능 면에서 큰 향상을 이루었다.");
insert into middledata(bdid,text,content) values(1,"모바일 어플리케이션","모바일 응용 소프트웨어는 스마트폰  태블릿 PC 등 모바일 장치에서 실행되는 응용 소프트웨어이다.");
insert into smalldata(bdid,mdid,text,content) values(1,16,"React Native","리액트 네이티브는 페이스북이 개발한 오픈 소스 모바일 애플리케이션 프레임워크이다. 안드로이드  iOS  웹  UWP용 애플리케이션을 개발하기 위해 사용되며  개발자들이 네이티브 플랫폼 기능과 더불어 리액트를 사용할 수 있게 한다. 완전하지 않은 Qt 포팅 또한 존재한다.");
insert into middledata(bdid,text,content) values(1,"데스크톱 어플리케이션","데스크톱 응용 프로그램(desktop application)은 단일 컴퓨터(노트북 또는 데스크톱)에 설치되고 특정 작업을 수행하는 데 사용할 수 있는 모든 소프트웨어를 의미한다. 일부 데스크톱 애플리케이션은 네트워크 환경에서 여러 사용자가 사용할 수도 있습니다.");
insert into smalldata(bdid,mdid,text,content) values(1,17,"Electron","Electron은 Chromium과 Node.js를 사용하여 JavaScript  HTML  CSS를 이용하여 윈도우 또는 Mac  리눅스에서 직접 설치하여 사용할 수 있는 애플리케이션을 개발 할 수 있다. 크로스 플랫폼 지원으로 이 세 개의 운영체제에서 빌드되고 동작한다.일렉트론은 청자오가 개발한 오픈 소스 프레임워크의 하나로  지금은 깃허브에 의해 개발되고 있다.");
insert into bigdata(text,content) values("웹 백엔드","백엔드 개발자는 프론트엔드  백엔드의 완전한 분리 구조를 지향하는 업무 스타일의 개발 방식으로서 백엔드의 DB와 API 서버 개발을 맡는다. 이와 다른 방식의 업무 스타일 직군으로는 웹퍼블리셔(UI 개발)와 개발자(서버 개발자)의 업무 분리 방식이 있다. 이 경우는 주로 서버사이드가 클라이언트를 감싸는 방식이고  전반적인 비지니스 로직은 개발자가 맡는다. MVC에서 Model  Controller가 백엔드의 소관이다.");
insert into middledata(bdid,text,content) values(2,"Internet","세계 최대 규모의 컴퓨터 통신망. 망의 망(network of networks) 형태로 되어 PC통신에서 모든 서비스를 제공하는 호스트같이 중심이 되는 컴퓨터는 없다. 인터넷에서 이용할 수 있는 서비스는 전자 우편(e-mail)  원격 컴퓨터 연결(telnet)  파일 전송 규약(FTP)  유즈넷 뉴스(Usenet News)  인터넷 정보 검색(Gopher)  인터넷 대화와 토론(IRC)  전자 게시판(BBS)  하이퍼텍스트 정보 열람(WWW:World Wide Web)  온라인 게임 등 다양하며 동화상이나 음성 데이터를 실시간으로 방송하는 서비스나 비디오 회의 등 새로운 서비스가 차례로 개발되어 이용 가능하게 되고 있다. 이와 같은 다양한 서비스와 풍부한 자원 때문에 인터넷을 정보의 바다라고 한다.");
insert into smalldata(bdid,mdid,text,content) values(2,18,"HTTP","인터넷의 월드 와이드 웹(WWW) 서버와 브라우저가 파일 등의 정보를 송수신하는 데 사용하는 클라이언트와 서버 사이의 통신 규약. 주로 하이퍼텍스트 마크업 언어(HTML) 문서를 주고 받는데 사용된다. ");
insert into smalldata(bdid,mdid,text,content) values(2,18,"DNS","TCP/IP 애플리케이션에서  ‘chollian.dacom.co.kr’와 같은 주 컴퓨터의 도메인 네임(domain name)을 ‘164.124.101.2’와 같은 IP 주소(Internet Protocol address)로 변환하고 라우팅 정보를 제공하는 분산형 데이터베이스 시스템. TCP/IP 네트워크에서는 호스트 컴퓨터를 식별하기 위해 도메인(영역이라는 의미)이라는 논리적 집단 또는 단위를 계층적으로 설정  이 논리적 집단 또는 단위의 이름을 호스트 이름의 일부로 조합해서 사용하는 방법이 채용되고 있다. 도메인은 표에 예시한 바와 같이 호스트 소속 기관의 종류와 소속 국가(지역)를 나타내는 도메인과 도메인 내의 세부 구분을 나타내는 부속 도메인(subdomain)으로 구성된다. 주 컴퓨터의 이름  소속 기관  소속 기관의 종류를 나타내는 도메인  소속 국가를 나타내는 도메인 등 보통 4개의 단어를 마침표(.)로 구분하여 표현한 것이 각 호스트 고유의 이름이며 이것을 도메인 네임이라고 한다. 도메인 네임은 호스트의 인터넷 주소(Internet address)라고도 한다. 도메인 네임은 인터넷상의 각종 서비스에 사용된다. 그런데 PC로 인터넷에 접속을 설정할 때 호스트의 도메인 네임에 대응하는 고유의 숫자 주소인 IP 주소가 필요한 경우가 있다. 통신하고자 하는 상대 호스트의 IP 주소를 모를 때 도메인 네임 시스템 (DNS)에 조회하면 DNS는 그 호스트의 도메인 네임을 IP 주소로 바꾸어 알려주는 역할을 한다.");
insert into smalldata(bdid,mdid,text,content) values(2,18,"Hosting","웹 서비스용 서버 임대 서비스. 대형 통신업체나 전문 회사가 자신들의 웹 서버를 개인 또는 개별 업체에 임대해 주는 서비스로 일반 개인이나 기업이 독자적으로 시설을 설치하고 운영하는 것보다 저렴한 비용으로 시설을 구축하고 관리할 수 있는 장점이 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,18,"Browser","인터넷에서 월드 와이드 웹 정보를 검색하는 데 사용되는 응용 프로그램. 웹 브라우저(web browser) 또는 브라우저라고도 한다. 브라우저 자체에는 인터넷 접속 기능이 포함되어 있지 않으나  윈도즈 95 등 인터넷 접속용의 소프트웨어가 설정되어 있기만 하면 브라우저의 기동만으로 전 세계 웹 서버(web server)에 있는 홈 페이지를 볼 수 있다. 문자나 화상은 물론 동화상  음악  게임 등을 지원하는 멀티미디어 검색 프로그램이다. 대표적인 브라우저로는 미국 구글사의 크롬(chrome)  마이크로소프트사의 인터넷 익스플로러(Internet Explorer)  모질라 재단의 자유 소프트웨어 파이어폭스(Firefox) 등을 들 수 있다.");
insert into middledata(bdid,text,content) values(2,"프론트엔드 지식","사용자와 시스템 간의 직접적인 접속되는 입력 부문 또는 데이터 처리 시스템의 시작점. 사용자 인터페이스(UI)  전단 처리기(FEP: front-end processor)  트랜잭션 모니터 등을 예로 들 수 있다. 이와 반대로 데이터베이스와 같이 시스템의 후면에서 시스템을 지원하는 부문을 후단부(back-end  백엔드)이라 한다.");
insert into smalldata(bdid,mdid,text,content) values(2,19,"HTML","인터넷 웹 페이지를 만들 때 사용하는 표준 마크업 언어. 제목(headings)  단락(paragraphs)  목록(lists)  링크(links) 등으로 텍스트의 구조적 의미를 나타내는 마크업 언어로  구조화된 문서를 만든다. 또 하이퍼텍스트 기능을 이용하여 외부 웹 페이지나 파일 등 다른 리소스에 연결하는 방법을 제공한다. 일반적으로 약어 에이치티엠엘(HTML)로 사용한다.");
insert into smalldata(bdid,mdid,text,content) values(2,19,"CSS","HTML  XML 등 마크업 언어로 작성된 웹 문서에서 레이아웃이나 구성 요소들의 스타일을 담당하는 언어. 웹 페이지의 개발과 수정을 용이하게 한다. 일반적으로 약어 시에스에스(CSS)로 사용한다.HTML  XHTML 등의 문서에서 내용과 스타일을 분리할 수 있으며  문서 내의 마크업 언어를 변경하지 않고도 해당 문서를 사용자의 환경이나 기기에 따라 다르게 보이도록 할 수 있다. 예를 들어  웹 문서 내 반복되는 제목이나 목록이 있을 경우  해당 부분을 일일이 지정하지 않고 CSS에서 일괄적으로 원하는 스타일을 적용할 수 있다. 일반적으로 CSS 문서와 HTML 문서를 별도로 구성하여 HTML 문서에서 CSS 문서를 링크하여 사용한다.");
insert into smalldata(bdid,mdid,text,content) values(2,19,"JavaScript","웹 페이지에서 사용자로부터 특정 이벤트나 입력 값을 받아 동적인 처리를 목적으로 고안된 객체 기반의 스크립트 프로그래밍 언어. 사용자 경험을 향상시키기 위한 방법으로 주로 사용된다. 일반적으로 HTML 문서에 내재되며  브라우저에서 실행된다. 예를 들면  마우스를 HTML 문서의 특정 이미지나 텍스트 위로 가져갔을 때 이미지 크기가 커지거나 텍스트의 색이 바뀌는 기능이다. 또 검색창 입력 박스에 ‘자’를 입력하면 현재 기준으로 사용자 검색이 많은 ‘자’로 시작하는 검색어 목록을 보여주는 기능도 자바스크립트로 구현한 것이다. 자바스크립트는 1995년 미국의 넷스케이프 커뮤니케이션즈(Netscape Communications)에서 처음 개발되었다. 자바스크립트 표준은 1996년 11월부터 유럽 컴퓨터 제조업자 협회(ECMA: European Computer Manufacturers Association) 기술위원회 39(TC-39)에서 에크마스크립트(ECMAScript  ECMA-262)라는 이름으로 개발되었다. 최신 표준은 2017년 6월에 제정된 ECMAScript 2017이며  2015년부터 1년에 한 번씩 새로운 버전의 ECMAScript를 공개하고 있다.");
insert into middledata(bdid,text,content) values(2,"OS/CS","컴퓨터의 기동과 함께 핵심부(kernel)가 주기억 장치에 올려져 컴퓨터를 작동시키고 운영을 관리하여 응용 프로그램이 효율적으로 실행될 수 있는 환경을 제공하는 기본 소프트웨어 초기의 OS는 중앙 처리 장치(CPU)  주기억 장치  디스크 장치  각종 입출력 장치 등이 정상적으로 작동하도록 감시하고 제어하며  응용 프로그램이 사용하는 CPU 시간  기억 공간  입출력 등 하드웨어 자원을 할당하고 배분하는 시스템 관리 기능이 기본이었으나  현재는 동영상이나 게임에 이르는 각종 응용 프로그램들을 수행할 수 있도록 유틸리티 기능들이 추가되고  사용자 인터페이스도 보다 편리한 그래픽 사용자 인터페이스(GUI)로 변모하고 있다. 하드 디스크 등 보조 기억 장치를 관리하는 파일 관리  응용 프로그램의 실행 순서  우선 순위 등을 관리하는 태스크(task) 관리와 작업(job) 관리  사용자의 식별  확인을 위한 보안 관리  컴퓨터 간의 정보 교환을 관리하는 통신망 관리 등 시스템 관리 기능은 사용자의 눈에 보이지 않는 곳에서 행해진다. OS는 여러 응용 프로그램 사이에 공통으로 이용될 수 있는 처리 기능을 제공한다. 문서 처리  표 계산 등 응용 프로그램에서 파일의 판독과 기록  화면상의 문자 표시  인쇄 등의 처리를 OS가 맡도록 하면 응용 프로그램을 간결하게 작성할 수 있다. OS는 이러한 공통 기능을 응용 프로그램 인터페이스(API)라는 함수로서 제공한다. 개인용 컴퓨터(PC)에 사용되는 대표적인 OS로는 MS사의 MS-DOS  윈도  IBM사의 OS/2  애플사의 맥 OS(Mac OS) 등이 있으며  워크스테이션급으로는 AT&T사가 개발한 유닉스(UNIX)  리눅스(Linux) 등이 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,20,"Terminal","디지털 자료 전송 시스템에서 자료를 만들거나 보기 위한 기기. 혹은 자료를 보내거나 받기 위한 기능을 수행하는 기기로서 사람과 직접 대면하게 되는 자료 처리의 기본 기기이다.");
insert into smalldata(bdid,mdid,text,content) values(2,20,"Basic Terminal Commands","프롬프트가 나타난 줄 위에 명령어를 입력하는 식의 컴퓨터 운용 체계나 응용 프로그램의 사용자 인터페이스. 예를 들면  윈도즈 운용 체계에서 MS-DOS로 빠져나가면 도스 프롬프트가 나타나는데 이때 각종 명령어를 사용하여 프로그램에 입력하는 것이다. 대부분 윈도즈나 Mac OS  BeOS 등은 그래픽 사용자 인터페이스(GUI)를 제공하고  유닉스 기반의 시스템은 CLI와 GUI를 제공한다.");
insert into smalldata(bdid,mdid,text,content) values(2,20,"Threads and Concurrency","스레드: 컴퓨터 프로그램 수행 시 프로세스 내부에 존재하는 수행 경로  즉 일련의 실행 코드. 프로세스는 단순한 껍데기일 뿐  실제 작업은 스레드가 담당한다. 프로세스 생");
insert into smalldata(bdid,mdid,text,content) values(2,20,"Memory Management","복수의 프로그램으로 공통적으로 사용되는 기억 영역의 사용 상황을 관리하며  각 사용자 프로그램으로부터의 요구에 따라 기억 할당을 하는 것. 전자 교환 방식에서는 주기억 장치상의 특정 영역에 용도 및 규모가 다른 몇 종류의 처리 영역을 설정(메모리 풀이라 한다)하여 일괄 관리하고 있는데  이것을 일시 기억(TM) 관리라고 한다. 각 사용자 프로그램으로부터 요구가 있으면 해당되는 처리 범위를 메모리 풀에서 포착하여 그 영역에 대응하는 플래그(flag)를 사용 중 표시로 하고  동시에 영역의 첫머리 주소를 출력한다.");
insert into smalldata(bdid,mdid,text,content) values(2,20,"I/O Management","");
insert into smalldata(bdid,mdid,text,content) values(2,20,"POSIX Basics","컴퓨터 처리 환경을 위한 이식 가능한 운영 체제 인터페이스로  IEEE P1003 기술 위원회에서 작성한 컴퓨터 운영 체제 서비스의 표준. 당초에는 유닉스 사용자 그룹이 유닉스를 기반으로 표준화 작업을 시작하였으나  미국의 국내 표준인 ANSI 표준으로 제정할 목적으로 IEEE가 인계하여 추진하게 되었다. P1003 기술 위원회는 여러 개의 작업반을 구성하여 주제별로 표준화를 추진한다. 대표적인 것으로는 ㉠ POSIX.0(포직스(POSIX) 개방 시스템 환경에 대한 전반적 지침)  ㉡ POSIX.1(커널(Kernel)  운영 체제 인터페이스)  ㉢ POSIX.2(셸(shell)과 툴(tool)  사용자 명령 등)  ㉣ POSIX.3(표준 규격의 적합성 검증법)  ㉤ POSIX.4(실시간 POSIX 확장)  ㉥ POSIX.5(에이다(Ada) 언어)  ㉦ POSIX.6(보안 기능) 등이다. 이 중에서 POSIX.1은 1988년에 ANSI/IEEE 1003.1 표준과 연방 정보 처리 표준(FIPS)으로 채택되고  이어서 1990년에 ISO/IEC 9945-1로서 국제 표준화되었다. POSIX라고 하면 POSIX.1을 가리키기도 한다. POSIX 표준은 유닉스를 기반으로 하였으나 다른 운영 체제에도 쉽게 이식될 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,20,"Interprocess Communication","프로세스 간의 통신 기법. 크게 공유 기억 장치 기법과 메시지 시스템 기법으로 나누어진다. 공유 기억 장치 기법은 통신하는 프로세스 간에 어떤 변수를 공유하여 이 공유 변수를 이용하여 정보를 교환하도록 한다. 메시지 시스템 방법은 통신을 제공하는 책임을 운영 체계가 가지고 프로세스가 메시지를 교환할 수 있도록 한다.");
insert into smalldata(bdid,mdid,text,content) values(2,20,"Basic Networking Concepts","넓은 뜻으로  지리적으로 떨어져 있는 다른 위치에 있는 장치(전화기  팩스  컴퓨터  단말기 등) 간에 정보를 교환할 수 있도록 이들 장치를 상호 접속하기 위하여 사용되는 전기 통신 기기와 장치  전송로의 결합. 전기 통신 기기와 장치에는 회선 다중화 장치  교환 기기  송수신 기기 등이 포함되고 전송로는 동선 케이블  광섬유  마이크로파 링크  통신 위성 등 다양한 매체로 구성된다. 네트워크는 사용되는 단말 장치 또는 서비스에 따라서 전신망  전화망  컴퓨터 통신망 등으로 발전해 왔으나 컴퓨터 처리와 통신의 결합으로 이들 간의 경계는 없어지고 종합 정보 통신망(ISDN)으로 발전하였으며  컴퓨터는 컴퓨터실의 경계를 훨씬 벗어나게 되어 분산 컴퓨터 처리를 가능하게 하였다. 좁은 의미로  컴퓨터 상호 간의 정보 교환과 정보 처리를 위한 데이터 통신망. 통신망 운영 체계  통신망 데이터베이스 등 네트워크 관련 용어는 대부분 컴퓨터 통신망과 관련하여 사용되는 용어이다. 네트워크의 규모에 따라 구내 정보 통신망(LAN)  대도시권 통신망(MAN)  광역 통신망(WAN)  세계적 통신망 등으로 분류된다.");
insert into smalldata(bdid,mdid,text,content) values(2,20,"OS works","컴퓨터의 기동과 함께 핵심부(kernel)가 주기억 장치에 올려져 컴퓨터를 작동시키고 운영을 관리하여 응용 프로그램이 효율적으로 실행될 수 있는 환경을 제공하는 기본 소프트웨어. 초기의 OS는 중앙 처리 장치(CPU)  주기억 장치  디스크 장치  각종 입출력 장치 등이 정상적으로 작동하도록 감시하고 제어하며  응용 프로그램이 사용하는 CPU 시간  기억 공간  입출력 등 하드웨어 자원을 할당하고 배분하는 시스템 관리 기능이 기본이었으나  현재는 동영상이나 게임에 이르는 각종 응용 프로그램들을 수행할 수 있도록 유틸리티 기능들이 추가되고  사용자 인터페이스도 보다 편리한 그래픽 사용자 인터페이스(GUI)로 변모하고 있다. 하드 디스크 등 보조 기억 장치를 관리하는 파일 관리  응용 프로그램의 실행 순서  우선 순위 등을 관리하는 태스크(task) 관리와 작업(job) 관리  사용자의 식별  확인을 위한 보안 관리  컴퓨터 간의 정보 교환을 관리하는 통신망 관리 등 시스템 관리 기능은 사용자의 눈에 보이지 않는 곳에서 행해진다. OS는 여러 응용 프로그램 사이에 공통으로 이용될 수 있는 처리 기능을 제공한다. 문서 처리  표 계산 등 응용 프로그램에서 파일의 판독과 기록  화면상의 문자 표시  인쇄 등의 처리를 OS가 맡도록 하면 응용 프로그램을 간결하게 작성할 수 있다. OS는 이러한 공통 기능을 응용 프로그램 인터페이스(API)라는 함수로서 제공한다. 개인용 컴퓨터(PC)에 사용되는 대표적인 OS로는 MS사의 MS-DOS  윈도  IBM사의 OS/2  애플사의 맥 OS(Mac OS) 등이 있으며  워크스테이션급으로는 AT&T사가 개발한 유닉스(UNIX)  리눅스(Linux) 등이 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,20,"Process Management","프로세스 관리는 현대의 운영 체제 가운데 중요한 부분이다. 운영 체제는 자원을 프로세스에 할당하고  프로세스가 정보를 공유하고 교환할 수 있게 하며  각 프로세스 자원을 다른 프로세스로부터 보호하며  프로세스 간 동기화를 가능하게 해야 한다. 이러한 요구를 충족하기 위하여 운영 체제는 각 프로세스를 위한 자료 구조를 관리하며  이로써 프로세스의 상태와 자원 소유권을 기술하고 운영 체제가 각 프로세스를 제어할 수 있다.");
insert into middledata(bdid,text,content) values(2,"프로그래밍 언어","컴퓨터의 프로그램을 작성하기 위해 고안된 언어로 여러 가지가 있다. 가장 간단한 언어는 기계어를 영문자의 기호로 치환한 어셈블리어이며  이 수준의 언어는 특정한 컴퓨터와 밀접하게 결부되어 있다. 또한 수준이 높은 언어는 컴퓨터와 독립적인 문법 구조를 가지며  이와 같은 처리 프로그램을 갖추고 있기만 하면 어떠한 컴퓨터라도 실행할 수 있도록 되어 있다. 이런 종류의 언어에는 포트란(FORTRAN: FORmula TRANslation)  알골(ALGOL: ALGOrithmic Language)  코볼(COBOL: COmmon Business Oriented Language)과 같은 일반적인 계산 처리용  보고서 작성용의 아르피지(RPG: Report Program Generator)  공학용의 다이너모(DYNAMO: DYNAmic MOdel)  MPS와 같은 응용 문제 언어  그래프 표시(Graphic Display)용 언어  시분할계(Time-sharing)용 언어 등도 있다. 이와 같이 언어 체계가 복잡하게 되고 컴퓨터가 취급하는 작업의 종류가 다양화됨에 따라서 컴퓨터의 운전을 관리하는 관리 프로그램도 복잡해져 관리 프로그램 자체가 제어 언어를 갖도록 되었다. 예를 들면 IBM의 작업 제어용 언어(Job-control Language)이다.");
insert into smalldata(bdid,mdid,text,content) values(2,21,"Rust","안전성  속도  병행성에 초점을 맞추어 모질라(Mozilla)에서 개발한 범용 시스템 프로그래밍 언어. C나 C++  Go 언어와 같은 시스템 프로그래밍 언어에 속하며  쓰레기 수집(garbage collection) 없이 안정적인 메모리 해제 기능을 지원한다. 또한 병행(concurrent) 프로그래밍 제공과 멀티 코어(multi-core) 프로세싱을 지원할 수 있는 병렬 처리 프로그래밍 제공이 강점이다. 공개 소스이다.");
insert into smalldata(bdid,mdid,text,content) values(2,21,"Go","멀티코어 지원  프로그램 생산성 향상을 목적으로 구글(Google)에서 개발한 범용 프로그래밍 언어 정적 타입 언어(static type language)로 설계하여 실행시간 효율성을 높이고  시스템 및 메모리 최적화  프로세스 병행 처리 등을 제공하여 효율성이 좋고  초보자도 배우기 쉽도록 프로그래밍 문법을 단순하게 하였다. 공개 소스이다.");
insert into smalldata(bdid,mdid,text,content) values(2,21,"Java","미국의 선 마이크로시스템즈사가 개발한 객체 지향 프로그래밍 언어. 1995년 5월에 발표  1996년 1월부터 정식 버전의 배포를 개시하였다. C++를 바탕으로 언어 규격을 규정하였다. 버그(bug)의 원인이 되기 쉬운 기능  예를 들면 포인터 연산을 생략하였다. 또 C++에서는 사용이 끝난 객체(object)를 명시적으로 폐기하도록 프로그램에 기술하였으나 그 대신 자동 쓰레기 수집(garbage collection) 기능을 추가하였다. 이와 같이 메모리 관리의 오류로 기인되는 버그를 방지한다. 자바는 객체 클래스의 계승(inheritance) 관계를 실행할 때에 확정하는 동적 모델을 채용하였다. C++에서는 컴파일 시에 확정하기 때문에 상위 클래스에 새로운 기능을 추가하면 그 클래스와 계승 관계가 있는 모든 클래스를 재컴파일해야 한다. 자바에서는 클래스의 계승 관계를 실행할 때 확정하기 때문에  하위의 클래스를 재컴파일할 필요가 없다. 자바로 작성한 프로그램의 원시 코드는 자바 컴파일러(Java compiler)로 컴파일한다. 자바 컴파일러는 바이트 코드라는 중간 코드를 생성한다. 이 중간 코드를 자바 가상 머신이라는 소프트웨어로 해석해서 실행한다. 이 소프트웨어를 실장한 컴퓨터라면 컴퓨터의 명령 집합 아키텍처나 운영 체계에 관계없이 같은 바이트 코드를 변경하지 않고 실행할 수 있다. 자바의 원시 코드를 고쳐 쓰거나 재컴파일할 필요가 없다. 그렇기 때문에 자바는 기종이나 운영 체계와 무관한 응용 프로그램의 개발 도구로 각광받고 있다. 자바 가상 머신의 실현 방법으로는 현재 3종류가 있다. 즉 바이트 코드를 해석하여 축차적으로 실행하는 자바 해석기  바이트 코드를 일괄적으로 2진 코드로 변환하고 실행하는 JIT 컴파일러(just in time compiler)  바이트 코드를 고속으로 해석하여 실행하기 위한 자바 칩(마이크로프로세서)이다.");
insert into smalldata(bdid,mdid,text,content) values(2,21,"C#","C++의 컴퓨팅 파워와 비주얼 베이직의 프로그래밍 편이성을 결합하기 위 한 목적으로 마이크로소프트사에서 개발한 새로운 객체 지향 프로그래밍 언 어. C++에 기반을 두고 Java와 비슷한 특색을 가지고 있으며  마이크로소프트의 .NET 플랫폼에서 쓰인다. 프로그래머가 각 단계별로 별도의 코드를 작성하지 않고서도 프로그래밍 객체에 접근할 수 있는 확장성 마크업 언어(XML)와 단순 객체 접근 프로토콜(SOAP)의 사용을 통해 프로그래밍을 단순화한 것이다.");
insert into smalldata(bdid,mdid,text,content) values(2,21,"PHP","웹 서버에서 사용되는 대표적인 스크립트 언어. 웹 개발 시 서버의 기능을 활용하기 위해 별도의 실행 파일을 만들 필요없이 PHP로 작성된 문서를 HTML 문서 안에 삽입하거나 별도의 문서로 만든다. 일반적으로 약어 피에이치피(PHP)로 사용한다. 웹 서비스를 개발하는 경우 HTML과 CSS  자바스크립트만으로도 가능하지만  서버 내에서 실행되어야 하는 서버 관련 기능은 구현하기가 어렵다. 예를 들어 서버와 클라이언트 간의 세션 관리  사용자가 요청하는 정보를 데이터베이스에서 추출 및 전달하는 등의 기능은 구현하는 데 한계가 있다. PHP는 이러한 한계를 극복하기 위해 개발되었다.");
insert into smalldata(bdid,mdid,text,content) values(2,21,"JavaScript","자바스크립트는 주로 웹 브라우저 내에서 사용되는 언어였으나  자바스크립트 기반의 런타임 플랫폼(예: Node.js)들이 개발되면서 서버측 프로그램 개발에도 사용이 크게 확대되었다.");
insert into smalldata(bdid,mdid,text,content) values(2,21,"Python","다양한 플랫폼에서 사용이 가능한 객체 지향 기반 인터프리터 방식의 고급 프로그래밍 언어 중 하나. 문법이 배우기 쉽고 결과를 바로 확인할 수 있어서 프로그래밍 초보자에게 추천되는 언어이다. 1991년에 귀도 반 로섬(Guido van Rossum)이 발표하였고  좋아하는 코미디 프로인 에서 파이선이라는 이름을 가져온 것으로 알려져 있다. 공동체 기반의 개방적 개발 모델을 가지고 있으며  파이선 소프트웨어 재단(Python Software Foundation)에서 관리한다. 문법 구조가 간단  명확하고  많은 시스템 호출과 풍부한 라이브러리(모듈)  다양한 윈도우 시스템용 인터페이스 등을 포함하고 있어 생산성이 높은 강력한 언어이기도 하다. 반면 실행 속도가 상대적으로 느리다는 단점이 있다. 다른 프로그래밍 언어와의 호환성이 높아  빠른 속도가 필요한 부분은 C  C++ 등의 언어로 개발하여 융합할 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,21,"Ruby","일본의 마츠모토 유키히로가 개발한 인터프리터 방식의 객체 지향 스크립트 언어. 스몰토크(Smalltalk)와 같은 순수 객체 지향 언어의 장점과 펄(Perl)의 텍스트 처리 능력  그리고 오크(awk)의 정규 표현 특성이 잘 결합된 공개 소프트웨어이다. 일반 변수나 식에는 형 선언이 없어 프로그램이 간결하여 유연하며  가비지 콜렉션(garbage collection)과 스레드(thread) 기능도 있다.");
insert into middledata(bdid,text,content) values(2,"버전 관리 시스템","버전 관리 소프트웨어. 복수의 이용자가 네트워크를 사용해서 소프트웨어를 개발하거나 문서를 작성하는 경우 파일 내용은 빈번히 변화하게 되는데  버전 관리 시스템은 변경된 이력(履歷)을 파일마다 관리할 수 있으며  동시에 여러 사람이 파일을 열 수 없도록 잠그는 기능을 지니고 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,22,"Basic Usage of Git","Git는 다음과 같은 체제를 갖고 있다. 일단 Git의 작업 폴더는 전체 기록과 각 기록을 추적할 수 있는 정보를 포함하고 있는 저장소이다. 즉 자기 컴퓨터에 모든 파일을 다 받아서 하는 셈. 위키로 치자면 위키 전체를 다 받아서 수정하는 것과 같다. 작업이 끝나면 Git 원격 저장소로 다시 발행하는데  여기에서 메인 저장소와 합치기 전 메인 저장소와 격리시키고 따로 개발 할 수 있는 가지라는걸 만들어 가지의 개발이 완료될 시 메인 저장소와 합치고 가지는 삭제시키는 가지치기를 할 수 있으며  또한 개발 중간중간 꼬리표를 매겨 개발을 더 수월하게 할 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,22,"GitHub","컴퓨터 프로그램 소스를 공유하고 협업하여 개발할 수 있는 버전 관리 시스템인 깃(Git)에 프로젝트 관리 지원 기능을 확장하여 제공하는 웹 호스팅 서비스. ");
insert into smalldata(bdid,mdid,text,content) values(2,22,"Gitlab","깃랩(GitLab)은 깃랩 사(GitLab Inc.)가 개발한 깃 저장소 및 CI/CD  이슈 추적  보안성 테스트 등의 기능을 갖춘 웹 기반의 데브옵스 플랫폼으로써  오픈 소스 라이선스 및 사유 소프트웨어 라이선스를 사용한다. 2019년 현재  깃 저장소와 이슈 추적 기능을 갖춘 유일한 단일 어플리케이션의 (Single Application) 데브옵스 솔루션이다. 시중에 유통되고 있는 많은 데브옵스 솔루션들은 자신들의 특화된 영역 이외는 API를 이용한 연동 만을 제공하지만 깃랩은 단일 어플리케이션으로써 데브옵스의 전 영역의 기능들을 모두 제공하고 있다.");
insert into middledata(bdid,text,content) values(2,"관계형 DB","관계들로 구성된 데이터베이스. 관계 데이터베이스의 데이터베이스 관리 시스템은 데이터 원소들을 재결합시켜 새로운 관계를 만들 수 있으며  이로 인해 데이터의 이용에 많은 다양성이 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,23,"PostgreSQL","PostgreSQL은 확장 가능성 및 표준 준수를 강조하는 객체-관계형 데이터베이스 관리 시스템(ORDBMS)의 하나이다. BSD 허가권으로 배포되며 오픈소스 개발자 및 관련 회사들이 개발에 참여하고 있다. 데이터베이스 서버로서 주요 기능은 데이터를 안전하게 저장하고 다른 응용 소프트웨어로부터의 요청에 응답할 때 데이터를 반환하는 것이이다. 소규모의 단일 머신 애플리케이션에서부터 수많은 동시 접속 사용자가 있는 대형의 인터넷 애플리케이션(또는 데이터 웨어하우스용)에 이르기까지 여러 부하를 관리할 수 있으며 macOS 서버의 경우 PostgreSQL은 기본 데이터베이스이다. 마이크로소프트 윈도우  리눅스(대부분의 배포판에서 제공됨)용으로도 이용 가능하다.");
insert into smalldata(bdid,mdid,text,content) values(2,23,"MySQL","표준 데이터베이스 질의 언어인 구조화된 질의 언어(SQL: Structured Query Language)를 사용하는 공개 소스의 관계형 데이터베이스 관리 시스템(RDBMS). 매우 빠르고  유연하며  사용하기 쉬운 특징이 있다. 다중 사용자  다중 스레드(thread)를 지원하고  C  C++  에펠(Eiffel)  자바  펄  PHP  파이선(Python) 스크립트 등을 위한 응용 프로그래밍 인터페이스(API)를 제공한다. 유닉스나 리눅스  윈도우 운영 체제 등에서 사용할 수 있다. 램프(LAMP)  즉 리눅스 운영 체제와 아파치(Apache) 서버 프로그램  MySQL  PHP 스크립트 언어 구성은 상호 연동이 잘되면서도 공개 소스(오픈 소스)로 개발되는 무료 프로그램이어서 홈 페이지나 쇼핑몰 등 일반적인 웹 개발에 널리 이용되고 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,23,"MariaDB","MariaDB는 오픈 소스의 관계형 데이터베이스 관리 시스템(RDBMS)이다. MySQL과 동일한 소스 코드를 기반으로 하며  GPL v2 라이선스를 따른다. 오라클 소유의 현재 불확실한 MySQL의 라이선스 상태에 반발하여 만들어졌으며  배포자는 몬티 프로그램 AB(Monty Program AB)와 저작권을 공유해야 한다. 이것은 MySQL과 높은 호환성을 유지하기 위함이며  MySQL API와 명령에 정확히 매칭하여  라이브러리 바이너리와 상응함을 제공하여 교체 가능성을 높이고자 함이다. 마리아 DB에는 새로운 저장 엔진인 아리아(Aria)뿐만 아니라  InnoDB를 교체할 수 있는 XtraDB 저장 엔진을 포함하고 있다. 이것은 트랜잭션과 비트랜잭션 엔진 그리고 미래에 나올 MySQL 판에 대응하고자 함일 것이다.");
insert into smalldata(bdid,mdid,text,content) values(2,23,"MS SQL","마이크로소프트 SQL 서버(영어: Microsoft SQL Server)는 마이크로소프트가 1989년 사이베이스(Sybase)를 기반으로 개발한 관계형 데이터베이스이다.");
insert into smalldata(bdid,mdid,text,content) values(2,23,"Oracle","Oracle SQL Developer는 Oracle 데이터 베이스에서 SQL과 함께 작업하기 위한 통합 개발 환경(IDE)이다.");
insert into middledata(bdid,text,content) values(2,"NoSQL DB","빅 데이터 처리을 위한 비관계형 데이터베이스 관리 시스템(DBMS). 전통적인 관계형 데이터베이스 관리 시스템(RDBMS)와는 다르게 설계된 비관계형(non-relational) DBMS로  대규모의 데이터를 유연하게 처리할 수 있는 것이 강점이다. 노에스큐엘(NoSQL)은 테이블-컬럼과 같은 스키마 없이  분산 환경에서 단순 검색 및 추가 작업을 위한 키 값을 최적화하고  지연(latency)과 처리율(throughput)이 우수하다. 그리고 대규모 확대가 가능한 수평적인 확장성의 특징을 가지고 있다. NoSQL에 기반을 둔 시스템의 대표적인 예로는 아파치 카산드라(Apache Cassandra)  하둡(Hadoop)  몽고디비(MongoDB) 등이 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,24,"MongoDB","대표적인 노에스큐엘(NoSQL) 데이터베이스 시스템. 몽고디비(MongoDB)는 데이터 교환 시 비산(BSON: Binary JSON) 문서 형태로 저장하여 여러 서버에 분산 저장 및 확장이 용이하며  방대한 데이터 처리가 빠르다는 장점이 있다. MongoDB는 C++ 언어로 작성되었으며  윈도우(Windows)  리눅스(Linux)  맥OS 등 다양한 운영 체제(OS)를 지원한다. 아페로 공용 라이선스(AGPL: Affero General Public License)에 따라 배포되는 공개 소스 소프트웨어(open source software)이다.");
insert into smalldata(bdid,mdid,text,content) values(2,24,"RethinkDB","RethinkDB는 같은 이름의 회사에서 만든 무료 오픈소스 분산 문서 지향 데이터베이스이다. 데이터베이스는 동적 스키마와 함께 JSON문서를 저장하며 쿼리 결과에 대한 실시간 업데이트를 애플리케이션에 푸시 할 수 있도록 설계되었다.");
insert into smalldata(bdid,mdid,text,content) values(2,24,"CouchDB","아파치 카우치DB(Apache CouchDB)는 스케일러블 아키텍처를 쉽게 이용하고 보유하는데 초점을 둔 오픈 소스 데이터베이스 소프트웨어이다. 도큐먼트 지향 NoSQL 데이터베이스 구조를 갖추고 있으며 얼랭으로 구현되어 있다. JSON을 사용하여 데이터를 저장하고  자바스크립트를 쿼리 언어로 사용(맵리듀스 사용)하며 API를 위해 HTTP를 사용한다.");
insert into smalldata(bdid,mdid,text,content) values(2,24,"DynamoDB","Amazon 에서 제공하는 데이터베이스 서비스");
insert into middledata(bdid,text,content) values(2,"DB 지식","데이터베이스를 다루는 다양한 기법  객체 관계 매핑(Object-relational mapping; ORM)  트랜잭션등 데이터베이스 구축 및 관리를 하는데 필요한 지식들");
insert into smalldata(bdid,mdid,text,content) values(2,25,"ORMs","객체 지향 프로그램 언어에서  상호 비호환형 시스템 간의 데이터를 변환하는 프로그램 기법의 하나. 객체와 테이블  또는 객체와 데이터베이스의 변형에 유연하게 대처할 수 있도록 해 준다.");
insert into smalldata(bdid,mdid,text,content) values(2,25,"ACID","데이터베이스에 대하여 원자성(Atomic)  일관성(Consistent)  고립성(Isolated)  영속성(Durable)을 가져야 하는 트랜잭션의 특성. 원자성은 더이상 분류할 수 없는 작업단위이며 트랜잭션내 모든 단계에서 모두 성공하거나 실패해야 하는 특성이며  정합성은 데이터베이스를 하나의 안정된 상태에서 다른 안정된 상태로 가져가야 하는 특성이며  고립성은 임의의 트랜잭션이 동시에 실행되는 다른 트랜잭션에 영향을 주어서는 안되는 특성이며  내구성은 무결성을 유지하기 위해 재구성될 수 있도록 로그를 드라이브에 유지해야 하는 특성이다.");
insert into smalldata(bdid,mdid,text,content) values(2,25,"Transactions","데이터베이스 트랜잭션(Database Transaction)은 데이터베이스 관리 시스템 또는 유사한 시스템에서 상호작용의 단위이다. 여기서 유사한 시스템이란 트랜잭션이 성공과 실패가 분명하고 상호 독립적이며  일관되고 믿을 수 있는 시스템을 의미한다. 이론적으로 데이터베이스 시스템은 각각의 트랜잭션에 대해 원자성(Atomicity)  일관성(Consistency)  독립성(Isolation)  영구성(Durability)을 보장한다. 이 성질을 첫글자를 따 ACID라 부른다. 그러나  실제로는 성능향상을 위해 이런 특성들이 종종 완화되곤 한다.어떤 시스템들에서는 트랜잭션들은 논리적 작업 단위(LUW  Logical Units of Work)로 불린다.");
insert into smalldata(bdid,mdid,text,content) values(2,25,"N+1 Problem","연관 관계에서 발생하는 이슈로 연과 관계가 설정된 엔티티를 조회할 경우에 조회된 데이터 갯수(n)만큼 연관관계의 조회 쿼리가 추가로 발생하여 데이터를 읽어오게 된다. 이를 N+1문제라고 한다.");
insert into middledata(bdid,text,content) values(2,"APIs","운영 체제  프로그래밍 언어 등에 있는 라이브러리를 응용 프로그램 개발 시 이용할 수 있도록 규칙들을 정의해 놓은 인터페이스. 파일 관리  화면 표시  프로그램 간 통신 등과 같이 자주 사용되는 다양한 공통 기능들을 OS나 프로그래밍 언어 등에서 라이브러리(library) 형태로 제공하는데  API는 라이브러리를 이용하는 방법과 형식을 규정한다. 즉  라이브러리는 기능을 구현한 함수들을 모아 놓은 것이고  API는 라이브러리를 이용할 수 있는 도구이다. API를 이용하면 개발자는 동작되는 기능의 세부 사항을 알 필요 없어 개발 시간과 비용  노력을 절감할 수 있다. 또한 OS 간 공통된 API 사용으로 다른 기종의 컴퓨터 사이에 응용 프로그램의 이식성(portability)이 확보된다.");
insert into smalldata(bdid,mdid,text,content) values(2,26,"REST","REST는 월드 와이드 웹과 같은 분산 하이퍼미디어 시스템을 위한 소프트웨어 아키텍처의 한 형식이다. 이 용어는 로이 필딩의 2000년 박사학위 논문에서 소개되었다. 필딩은 HTTP의 주요 저자 중 한 사람이다. 이 개념은 네트워킹 문화에 널리 퍼졌다.");
insert into smalldata(bdid,mdid,text,content) values(2,26,"JSON APIs","JSON(JSON은 속성-값 쌍 또는 키-값 쌍으로 이루어진 데이터 오브젝트를 전달하기 위해 인간이 읽을 수 있는 텍스트를 사용하는 개방형 표준 포맷이다. 비동기 브라우저/서버 통신 을 위해  넓게는 XML을 대체하는 주요 데이터 포맷이다.) 또는 JavaScript Object Notation은 정의된 방식으로 통신하는 서버와 통신하기 위해 각 응용 프로그램에 애드혹 코드가 필요하지 않도록 설계된 인코딩 체계이다. JSON API 모듈은 엔티티 유형  번들 및 필드와 같은 데이터 저장소 및 데이터 구조를 구현합니다");
insert into middledata(bdid,text,content) values(2,"Authentication","다중 사용자 컴퓨터 시스템 또는 망 운용 시스템에서  시스템이 단말 작동 개시(log-on) 정보를 확인하는 보안 절차. 인증에는 1. 망을 경유해서 컴퓨터에 접속해 오는 사용자가 등록된(허가받은) 사용자인지를 확인하는 것과  2. 전송된 메시지(통신문)가 변조되거나 전와(轉訛)되지 않은 송신자가 보낸 그대로의 것인지를 확인하는 것이 있다. 1 의 경우  사용자의 성명과 패스워드를 허가받은 사용자 목록과 대조하여 일치하면 사용자가 컴퓨터 시스템에 접속하는 것을 허용하되 그 사용자의 접속 카테고리나 사용자 계정상의 지정된 범위까지만 접속을 허용한다. 2 의 경우  메시지 인증 부호  암호  디지털 서명 부호 등이 사용된다.");
insert into smalldata(bdid,mdid,text,content) values(2,27,"OAuth","OAuth는 인터넷 사용자들이 비밀번호를 제공하지 않고 다른 웹사이트 상의 자신들의 정보에 대해 웹사이트나 애플리케이션의 접근 권한을 부여할 수 있는 공통적인 수단으로서 사용되는  접근 위임을 위한 개방형 표준이다.");
insert into smalldata(bdid,mdid,text,content) values(2,27,"Basic Authentication","HTTP 트랜잭션의 컨텍스트에서 기본 액세스 인증은 HTTP 사용자 에이전트가 요청시 사용자 이름과 비밀번호를 제공하는 방법입니다. 기본 HTTP 인증에서 요청에는 Authorization : Basic <credentials> 형식의 헤더 필드가 포함됩니다.");
insert into smalldata(bdid,mdid,text,content) values(2,27,"Token Authentication","보안 토큰(또는 인증 토큰이라고도 함)은 소유자가 네트워크 서비스에 대한 액세스를 허가하기 위해 휴대하는 소형 하드웨어 장치입니다. 장치는 스마트 카드 형태이거나 키 리모컨과 같이 일반적으로 사용되는 물체에 내장될 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,27,"JWT","JSON 웹 토큰은 선택적 서명 및 선택적 암호화를 사용하여 데이터는 만들기 위한 인터넷 표준으로  페이로드는 몇몇 클레임 표명을 처리하는 JSON을 보관하고 있다. 토큰은 비공개 시크릿 키 또는 공개/비공개 키를 사용하여 서명된다.");
insert into middledata(bdid,text,content) values(2,"캐싱","캐싱이란 일반적으로 일시적인 특징이 있는 데이터 하위 집합을 저장하는 고속 데이터 스토리지 계층을 말한다. 따라서 이후에 해당 데이터에 대한 요청이 있을 경우 데이터의 기본 스토리지 위치에 액세스할 때보다 더 빠르게 요청을 처리할 수 있다. 캐싱을 사용하면 이전에 검색하거나 계산한 데이터를 효율적으로 재사용할 수 있다. 작동방식은 다음과 같다. 캐시의 데이터는 일반적으로 RAM과 같이 빠르게 액세스할 수 있는 하드웨어에 저장되며  소프트웨어 구성 요소와 함께 사용될 수도 있다. 캐시의 주요 목적은 더 느린 기본 스토리지 계층에 액세스해야 하는 필요를 줄임으로써 데이터 검색 성능을 향상하는 것이다. 속도를 위해 용량을 절충하는 캐시는 일반적으로 데이터의 하위 집합을 일시적으로 저장한다. 보통 완전하고 영구적인 데이터가 있는 데이터베이스와는 대조적이다.");
insert into smalldata(bdid,mdid,text,content) values(2,28,"CDN","콘텐츠 전송 네트워크는 콘텐츠를 효율적으로 전달하기 위해 여러 노드를 가진 네트워크에 데이터를 저장하여 제공하는 시스템을 말한다. 인터넷 서비스 제공자에 직접 연결되어 데이터를 전송하므로  콘텐츠 병목을 피할 수 있는 장점이 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,28,"Redis","레디스는 Remote Dictionary Server의 약자로서  키-값 구조의 비정형 데이터를 저장하고 관리하기 위한 오픈 소스 기반의 비관계형 데이터베이스 관리 시스템이다. 2009년 살바토르 산필리포가 처음 개발했다. 2015년부터 Redis Labs가 지원하고 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,28,"Memcached","Memcached 는 범용 분산 캐시 시스템이다. 외부 데이터 소스의 읽기 횟수를 줄이기 위해 데이터와 객체들을 RAM에 캐시 처리함으로써 동적 데이터베이스 드리븐 웹사이트의 속도를 높이기 위해 종종 사용된다.");
insert into smalldata(bdid,mdid,text,content) values(2,28,"Client Side","client side caching은 브라우저 응용 프로그램 또는 다른 클라이언트(예: 중간 네트워크 캐시) 내에서 직접 이전에 요청된 파일의 데이터를 복제합니다. 클라이언트 캐시는 브라우저가 웹 서버와 통신하지 않고도 파일에 액세스할 수 있도록 해주기 때문에 가장 효율적인 캐시 유형입니다.");
insert into middledata(bdid,text,content) values(2,"웹 보안","비승인된 접근  사용  수정  파괴  중단으로부터 웹사이트를 보호하는 행동 또는 실행을 가리킨다.");
insert into smalldata(bdid,mdid,text,content) values(2,29,"MD5","MD5는 128비트 암호화 해시 함수이다. RFC 1321로 지정되어 있으며  주로 프로그램이나 파일이 원본 그대로인지를 확인하는 무결성 검사 등에 사용된다. 1991년에 로널드 라이베스트가 예전에 쓰이던 MD4를 대체하기 위해 고안했다.");
insert into smalldata(bdid,mdid,text,content) values(2,29,"SHA Family","SHA 함수들은 서로 관련된 암호학적 해시 함수들의 모음이다. 이들 함수는 미국 국가안보국이 1993년에 처음으로 설계했으며 미국 국가 표준으로 지정되었다.");
insert into smalldata(bdid,mdid,text,content) values(2,29,"scrypt","암호화에서 scrypt는 원래 Tarsnap 온라인 백업 서비스를 위해 Colin Percival에서 만든 암호 기반 키 파생 기능입니다. 이 알고리즘은 대량의 메모리를 요구하여 대규모 사용자 지정 하드웨어 공격을 수행하는 데 비용이 많이 들도록 특별히 설계되었습니다.");
insert into smalldata(bdid,mdid,text,content) values(2,29,"bcrypt","bcrypt는 Blowfish 암호를 기반으로 1999 년 USENIX에서 발표 된 Niels Provos와 David Mazi?res가 암호 해싱 기능입니다. 레인보우 테이블 공격으로부터 보호하기 위해  salt(암호화에서 솔트는 데이터  비밀번호 또는 비밀번호 문구를 해시하는 단방향 함수에 대한 추가 입력으로 사용되는 임의의 데이터입니다. 소금은 보관시 비밀번호를 보호하는 데 사용됩니다.)을 통합하는 것 외에도 bcrypt는 시간이 지남에 따라 반복되는 함수입니다.");
insert into smalldata(bdid,mdid,text,content) values(2,29,"HTTPS","HTTPS는 월드 와이드 웹 통신 프로토콜인 HTTP의 보안이 강화된 버전이다. HTTPS는 통신의 인증과 암호화를 위해 넷스케이프 커뮤니케이션즈 코퍼레이션이 개발했으며  전자 상거래에서 널리 쓰인다.");
insert into smalldata(bdid,mdid,text,content) values(2,29,"CORS","교차 출처 리소스 공유  교차 출처 자원 공유는 웹 페이지 상의 제한된 리소스를 최초 자원이 서비스된 도메인 밖의 다른 도메인으로부터 요청할 수 있게 허용하는 구조이다. 웹페이지는 교차 출처 이미지  스타일시트  스크립트  iframe  동영상을 자유로이 임베드할 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,29,"Content Security Policy","콘텐츠 보안 정책은 신뢰된 웹 페이지 콘텍스트에서 악의적인 콘텐츠를 실행하게 하는 사이트 간 스크립팅  클릭재킹  그리고 기타 코드 인젝션 공격을 예방하기 위해 도입된 컴퓨터 보안 표준이다.");
insert into smalldata(bdid,mdid,text,content) values(2,29,"SSL/TLS","전송 계층 보안는 컴퓨터 네트워크에 통신 보안을 제공하기 위해 설계된 암호 규약이다. 그리고 트랜스포트 레이어 보안이라는 이름은 보안 소켓 레이어가 표준화 되면서 바뀐 이름이다.");
insert into smalldata(bdid,mdid,text,content) values(2,29,"OWASP Security Risks","");
insert into middledata(bdid,text,content) values(2,"Testing","1. 수동 또는 자동 수단을 통하여 시험자료 집합에 대해 프로그램을 직접 수행시켜 봄으로써 프로그램의 동작을 검사하는 과정. 2. 수동 또는 자동수단을 통하여 시스템이 특정요구를 만족하는가를 검증하고 기대결과와 실제결과의 차이점을 확인함으로써 시스템 또는 시스템 구성요소를 평가하는 과정. 오류수정과 비교된다.");
insert into smalldata(bdid,mdid,text,content) values(2,30,"Integration Testing","통합 시험은 단위 시험이 끝난 소프트웨어를 결합해 가며 시험 하는 방법이다. 단위 시험이 끝난 모듈들을 좀 더 큰 단위의 집합으로 통합 구성한 후  통합 시험 계획에 따라서 테스트를 수행한다. 통합 시험을 통과한 모듈 집합은 시스템 검사 단계의 테스트 대상으로서 넘어가게 된다. ");
insert into smalldata(bdid,mdid,text,content) values(2,30,"Unit Testing","유닛 테스트는 컴퓨터 프로그래밍에서 소스 코드의 특정 모듈이 의도된 대로 정확히 작동하는지 검증하는 절차다. 즉  모든 함수와 메소드에 대한 테스트 케이스를 작성하는 절차를 말한다.");
insert into smalldata(bdid,mdid,text,content) values(2,30,"Functional Testing","기능 테스트는 품질 보증 프로세스의 한 과정이며  테스트 대상 컴포넌트의 명세로부터 도출된 테스트 케이스에 기반하여 수행되는 블랙박스 테스트 기법의 일종이다. 프로그램에 입력을 주고  그에 해당하는 출력을 조사함으로써 테스트를 수행하게 된다.");
insert into middledata(bdid,text,content) values(2,"CI/ CD","CI/CD는 애플리케이션 개발 단계를 자동화하여 애플리케이션을 보다 짧은 주기로 고객에게 제공하는 방법이다. CI/ CD의 기본 개념은 지속적인 통합  지속적인 서비스 제공  지속적인 배포이다. CI/ CD는 새로운 코드 통합으로 인해 개발 및 운영팀에 발생하는 문제(일명 인테그레이션 헬)을 해결하기 위한 솔루션이다.");
insert into middledata(bdid,text,content) values(2,"소프트웨어 설계원칙","소프트웨어 설계 원칙은 코드의 구성요소를 구성 및 엔지니어링하는 데 사용되는 지침이다. 코드를 구현하기 전에 구조화하는 방법을 계획하는 것은 매우 중요한데 잘 설계된 코드를 구현하면 강력하고 유연하며 테스트 가능성과 확장 가능성이 높은 구성요소를 만들 가능성이 커지기 때문이다. ");
insert into smalldata(bdid,mdid,text,content) values(2,32,"SOLID","S - Single-responsiblity Principle O - Open-closed Principle L - Liskov Substitution Principle I - Interface Segregation Principle D - Dependency Inversion Principle");
insert into smalldata(bdid,mdid,text,content) values(2,32,"KISS","KISS 원칙이란 디자인에서 간단하고 알기 쉽게 만드는 편이 좋다는 원리를 말한다.");
insert into smalldata(bdid,mdid,text,content) values(2,32,"YAGNI","YAGNI는 프로그래머가 필요하다고 간주할 때까지 기능을 추가하지 않는 것이 좋다는 익스트림 프로그래밍의 원칙이다. 익스트림 프로그래밍의 공동 설립자 론 제프리스는 다음과 같이 썼다: 실제로 필요할 때 무조건 구현하되  그저 필요할 것이라고 예상할 때에는 절대 구현하지 말라.");
insert into smalldata(bdid,mdid,text,content) values(2,32,"DRY","중복 배제 (Dont repeat yourself; DRY)는 소프트웨어 개발 원리의 하나로  모든 형태의 정보 중복을 지양하는 원리이다. 특히 다층 구조 시스템에서 유용하다. 중복배제 원리는 한마디로 “모든 지식은 시스템 내에서 유일하고 중복이 없으며 권위있는 표상만을 가진다”는 말로 기술할 수 있다.");
insert into middledata(bdid,text,content) values(2,"Search Engine","인터넷 웹 사이트에 저장된 각종 정보를 수집하고  검색을 통하여 사용자가 원하는 데이터를 찾을 수 있는 소프트웨어.1989년 팀 버너스리가 웹을 제안한 뒤 1990년대 웹 사용 초기에는 웹 서버의 목록을 제공하는 웹 사이트를 사용하였다. 이후 웹 서버 목록이 수천여 개 이상으로 증가하며 이를 효율적으로 검색할 수 있는 검색 엔진이 개발되었다. 웹 검색 엔진은 크게 웹 사이트를 검색하여 해당 정보를 수집하는 로봇 프로그램과 수집된 자료의 색인을 만드는 색인 프로그램  검색어에 따른 검색 결과를 출력하는 검색 프로그램으로 구성된다. 로봇 프로그램은 평소 인터넷을 검색하여 검색이 허용된 모든 웹 사이트의 정보들을 축적하며  웹 사이트는 메타 태그(meta tag)를 선언해 둠으로써 로봇의 검색을 거부할 수도 있다. 색인 프로그램은 축적된 웹 사이트의 정보를 분석하여  사용자의 검색 요구에 따라 빠르게 데이터를 찾을 수 있도록 정보를 저장한다. 색인은 사이트를 주기적으로 재방문하여 업데이트된다.검색 프로그램은 사용자의 검색어에 따라 색인 정보를 탐색하여  가장 적합한 정보 목록을 제공한다. 웹 검색 엔진은 검색 서비스 제공자에 따라 서로 다른 알고리듬이 사용될 수 있으며  자주 사용되는 단어  메타 태그의 키워드 등 특별한 색인 정보에 별도의 가중치를 두어 검색 결과를 제공할 수 있다. 네이버  구글  야후 등 대표적인 포털 사이트는 웹 검색 엔진을 제공하고 있으며  광고주의 검색 결과를 상위에 노출시키는 등 웹 검색 엔진을 활용한 광고 사업으로 수익을 창출한다.");
insert into smalldata(bdid,mdid,text,content) values(2,33,"Elasticsearch","일래스틱서치는 루씬 기반의 검색 엔진이다. HTTP 웹 인터페이스와 스키마에서 자유로운 JSON 문서와 함께 분산 멀티테넌트 지원 전문 검색 엔진을 제공한다. 일래스틱서치는 자바로 개발되어 있으며 아파치 라이선스 조항에 의거하여 오픈 소스로 출시되어 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,33,"Solr","Solr은 Apache Lucene 프로젝트에서 Java로 작성된 오픈 소스 엔터프라이즈 검색 플랫폼입니다. 주요 기능으로는 전체 텍스트 검색  적중 강조 표시  패싯 검색  실시간 인덱싱  동적 클러스터링  데이터베이스 통합  NoSQL 기능 및 풍부한 문서 처리가 있습니다.");
insert into middledata(bdid,text,content) values(2,"아키텍처 패턴","아키텍처 패턴(architectural pattern)은 주어진 문맥 안에서 소프트웨어 아키텍처의 공통적인 발생 문제에 대한 일반적인  재사용 가능한 해결책을 의미한다. 아키텍처 패턴은 소프트웨어 디자인 패턴과 비슷하지만 더 넓은 범위에 속한다. 아키텍처 패턴은 소프트웨어 공학의 다양한 문제를 해결하는데  예를 들어 컴퓨터 하드웨어 성능 제한  비즈니스 위험의 최소화와 고가용성을 들 수 있다. 일부 아키텍처 패턴은 소프트웨어 프레임워크 안에 구현되어 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,34,"Monolithic Apps","소프트웨어 엔지니어링에서 Monolithic application 은 사용자 인터페이스와 데이터 액세스 코드가 단일 플랫폼에서 단일 프로그램으로 결합되는 단일 계층 소프트웨어 애플리케이션을 설명합니다. 모 놀리 식 애플리케이션은 독립적이며 다른 컴퓨팅 애플리케이션과 독립적입니다.");
insert into smalldata(bdid,mdid,text,content) values(2,34,"Microservices","마이크로서비스는 애플리케이션을 느슨하게 결합된 서비스의 모임으로 구조화하는 서비스 지향 아키텍처 스타일의 일종인 소프트웨어 개발 기법이다. 마이크로서비스 아키텍처에서 서비스들은 섬세하고 프로토콜은 가벼운 편이다.");
insert into smalldata(bdid,mdid,text,content) values(2,34,"SOA","서비스 지향 아키텍처란 대규모 컴퓨터 시스템을 구축할 때의 개념으로 업무상의 일 처리에 해당하는 소프트웨어 기능을 서비스로 판단하여 그 서비스를 네트워크상에 연동하여 시스템 전체를 구축해 나가는 방법론이다");
insert into smalldata(bdid,mdid,text,content) values(2,34,"Serverless","서버리스 컴퓨팅은 클라우드 컴퓨팅 실행 모델의 하나로  클라우드 제공자는 동적으로 머신 자원의 할당을 관리한다. 가격은 미리 구매한 용적 단위가 아닌 애플리케이션이 소비한 자원의 실제 양에 기반을 둔다. 유틸리티 컴퓨팅의 일종이다.");
insert into middledata(bdid,text,content) values(2,"메시지 브로커","메시지 브로커(message broker)  인터페이스 엔진(interface engine)은 송신자의 이전 메시지 프로토콜로부터의 메시지를 수신자의 이전 메시지 프로토콜로 변환하는 중간 컴퓨터 프로그램 모듈이다. 메시지 브로커들은 응용 소프트웨어가 이전에 정의해둔 메시지를 교환할 수 있는 전기통신의 요소 또는 컴퓨터 네트워크이다. 메시지 브로커들은 메시지 지향 미들웨어(MOM)의 빌딩 블록이지만 일반적으로 MOM과 원격 프로시저 호출(RPC) 등의 전통적인 미들웨어를 대체하지는 않는다.");
insert into smalldata(bdid,mdid,text,content) values(2,35,"RabbitMQ","RabbitMQ는 오픈 소스 메시지 브로커 소프트웨어로서  AMQP를 구현하였으며 그 이후로 STOMP  MQTT 등의 프로토콜을 지원하기 위해 플러그인 구조와 함께 확장되고 있다");
insert into smalldata(bdid,mdid,text,content) values(2,35,"Kafka","아파치 카프카는 아파치 소프트웨어 재단이 스칼라로 개발한 오픈 소스 메시지 브로커 프로젝트이다. 이 프로젝트는 실시간 데이터 피드를 관리하기 위해 통일된  높은 처리량  낮은 지연시간을 지닌 플랫폼을 제공하는 것이 목표이다. ");
insert into middledata(bdid,text,content) values(2,"Containerization","새로 만든 애플리케이션을  실제 작업환경에서 테스트 환경  운영환경으로 옮길 때 마다 발생하는 오류를 최소화 하기위해 실행에 필요한 라이브러리  바이너리  구성 파일 등을 패키지로 묶어 배포하는 것이다. 운영체제를 제외하고 애플리케이션 실행에 필요한 모든 파일을 패키징한다는 점에서 운영체제 위에서 구현된 가상화  즉 운영체제 레벨 가상화라고 부르기도 한다.");
insert into smalldata(bdid,mdid,text,content) values(2,36,"Docker","도커는 리눅스의 응용 프로그램들을 소프트웨어 컨테이너 안에 배치시키는 일을 자동화하는 오픈 소스 프로젝트이다. 도커 웹 페이지의 기능을 인용하면 다음과 같다: 도커 컨테이너는 일종의 소프트웨어를 소프트웨어의 실행에 필요한 모든 것을 포함하는 완전한 파일 시스템 안에 감싼다.");
insert into middledata(bdid,text,content) values(2,"Virtualization","가상화 기술이란 하드웨어에 종속된 컴퓨터 리소스를 추상화하여 서버  스토리지  네트워크 등의 소프트웨어 IT 서비스를 생성하는 솔루션을 의미한다. 가상화를 사용하면 물리적 머신의 기능을 여러 사용자 또는 환경에 배포해 물리적 머신을 최대한 활용할 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,37,"W","");
insert into middledata(bdid,text,content) values(2,"GraphQL","그래프QL은 페이스북이 2012년에 개발하여 2015년에 공개적으로 발표된 데이터 질의어이다. 그래프QL은 REST 및 부속 웹서비스 아키텍쳐를 대체할 수 있다. 클라이언트는 필요한 데이터의 구조를 지정할 수 있으며  서버는 정확히 동일한 구조로 데이터를 반환한다. 그래프QL은 사용자가 어떤 데이터가 필요한 지 명시할 수 있게 해 주는 강타입 언어이다. 이러한 구조를 통해 불필요한 데이터를 받게 되거나 필요한 데이터를 받지 못하는 문제를 피할 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,38,"Apollo","");
insert into smalldata(bdid,mdid,text,content) values(2,38,"Relay Modern","릴레이는 데이터 기반 애플리케이션을 구축하기 위한 JavaScript 프레임워크입니다. 합성 가능한 사용자 인터페이스를 위한 React와 합성 가능한 데이터 페팅을 위한 GraphQL을 결합한다.");
insert into middledata(bdid,text,content) values(2,"Graph Databases","컴퓨팅에서 그래프 데이터베이스는 시맨틱 쿼리를 위해 노드  엣지  프로퍼티와 함께 그래프 구조를 사용하여 데이터를 표현하고 저장하는 데이터베이스이다. 이 시스템의 주 개념은 그래프(엣지 또는 관계)이며 스토어에 직접 데이터 항목들의 관계를 정한다. 이러한 관계들은 스토어 안의 데이터가 함께 직접 연결될 수 있게 한다.");
insert into middledata(bdid,text,content) values(2,"Web Servers","웹 콘텐츠를 저장하거나 처리하는 컴퓨터 또는 소프트웨어. 일반적으로 웹 서버가 되는 컴퓨터에 설치되는 소프트웨어를 말한다.웹 서버 소프트웨어는 HTTP 프로토콜을 통해 클라이언트(웹 브라우저)의 요청 정보를 받아 처리하고 그 결과를 다시 클라이언트에 보낸다. 클라이언트가 요청하는 자원(리소스)을 URL(Uniform Resource Locator) 형태로 받아 내부 파일 시스템과 매핑하여 처리하거나  URL과 입력 값(예: 로그인 화면의 아이디  패스워드 등)을 함께 받으면 사전에 약속된 처리를 한 후 그 결과를 클라이언트에 전달한다. 대표적인 웹 서버로는 아파치(Apache)  엔진엑스(nginx)  마이크로소프트사의 IIS(Internet Information Services) 등이 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,40,"Nginx","Nginx는 웹 서버 소프트웨어로  가벼움과 높은 성능을 목표로 한다. 웹 서버  리버스 프록시 및 메일 프록시 기능을 가진다.");
insert into smalldata(bdid,mdid,text,content) values(2,40,"Apache","아파치 HTTP 서버는 아파치 소프트웨어 재단에서 관리하는 HTTP 웹 서버이다. BSD  리눅스 등 유닉스 계열 뿐 아니라 마이크로소프트 윈도우나 노벨 넷웨어 같은 기종에서도 운용할 수 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,40,"Caddy","Caddy 웹 서버는 Go로 작성된 오픈 소스 웹 서버입니다. HTTP 기능에 Go 표준 라이브러리를 사용하고 기본적으로 HTTPS를 지원합니다. 저자 인 Matthew Holt는 2014 년 12 월에 Caddy를 개발하기 시작하여 2015 년 4 월에 출시했습니다.");
insert into smalldata(bdid,mdid,text,content) values(2,40,"MS IIS","마이크로소프트 인터넷 정보 서비스는 마이크로소프트 윈도우를 사용하는 서버들을 위한 인터넷 기반 서비스들의 모임이다. 이전 이름은 인터넷 정보 서버였다");
insert into middledata(bdid,text,content) values(2,"sustainability ","컴퓨터 과학을 활용하여 사회  경제 및 환경 자원을 최적화화려는 광범위한 분야이다. 대표적인 응용프로그램으로 재생 가능한 자원과 저장 기능을 구현하여 에너지 생산과 지출을 통제하는 스마트 그리드  도로 상황을 분석하고 정보를 운전자에게 중계하여 현명한 의사결정을 내리도록 돕는 지능형 교통시스템이 있다.");
insert into smalldata(bdid,mdid,text,content) values(2,41,"Mitigation Strategies","위험 완화 전략은 정의에 따라 위험(충격의 심각성 및/또는 발생 확률)을 줄이기 위한 조치를 취하는 것이다. 요율이 빠르게 변화하고 그 시점에서 위험을 줄이는 것이 훨씬 더 많은 비용이 드는 경우 손실이 빠르게 누적될 수 있습니다.");
insert into smalldata(bdid,mdid,text,content) values(2,41,"Migration Strategies","데이터 마이그레이션은 능동적인 비즈니스 처리를 중단하거나 실행 중지하지 않고 데이터 복사본을 만들어 한 장치 또는 시스템에서 다른 장치로 이동하는 프로세스입니다.");
insert into smalldata(bdid,mdid,text,content) values(2,41,"Horizontal scaling","수평적 확장이란 리소스 풀에 더 많은 시스템(예: 스케일아웃이라고도 함)을 추가함으로써 확장되는 것을 의미하며  수직적 확장이란 기존 시스템에 더 많은 전력(예: CPU  RAM)을 추가하여 확장(예: 스케일업이라고도 함)하는 것을 의미합니다.");
insert into smalldata(bdid,mdid,text,content) values(2,41,"Vertical Scaling","수직적 확장이란 필요에 따라 서버에 리소스(CPU/RAM/DISK)를 더 추가하는 것을 말합니다(데이터베이스 또는 애플리케이션 서버는 여전히 하나임). 수직 스케일링은 중소기업뿐 아니라 중견기업의 애플리케이션 및 제품에 가장 많이 사용된다.");




